Bash(node --env-file=.env.local --input-type=module << 'EOF'
import { createClient } from "@supabase/supabase-js";

const url  = process.env.NEXT_PUBLIC_SUPABASE_URL;
const key  = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY;

console.log\("URL :", url\);
console.log\("KEY :", key?.slice\(0, 30\) + "…"\);

const db = createClient\(url, key\);

const { data: accounts, error: e1 } = await db.from\("accounts"\).select\("*"\).order\("bank_name"\);
console.log\("\\n— accounts —"\);
console.log\("error :", e1\);
console.log\("count :", accounts?.length\);
console.log\("first :", accounts?.[0]\);

const { data: txns, error: e2 } = await db
  .from\("transactions"\)
  .select\("*"\)
  .gte\("date", "2026-02-01"\)
  .lt\("date",  "2026-03-01"\)
  .order\("date", { ascending: false }\);
console.log\("\\n— transactions —"\);
console.log\("error :", e2\);
console.log\("count :", txns?.length\);
console.log\("first :", txns?.[0]\);

const { data: budgets, error: e3 } = await db
  .from\("budgets"\)
  .select\("*"\)
  .eq\("month", 2\).eq\("year", 2026\);
console.log\("\\n— budgets —"\);
console.log\("error :", e3\);
console.log\("count :", budgets?.length\);
EOF)
